   node_uname_info{cluster=~"$cluster", nodename=~"$instance"})

round(
max(
(sum(node_filesystem_size_bytes{cluster=~"$cluster", job=~"$job"}) by (instance, device) - sum(node_filesystem_free_bytes{cluster=~"$cluster", job=~"$job"}) by (instance, device)) /
(sum(node_filesystem_size_bytes{cluster=~"$cluster", job=~"$job"}) by (instance, device) - sum(node_filesystem_free_bytes{cluster=~"$cluster", job=~"$job"}) by (instance, device) +
sum(node_filesystem_avail_bytes{cluster=~"$cluster", job=~"$job"}) by (instance, device))
 * 100 
 * on(instance) group_left(nodename) 
   node_uname_info{cluster=~"$cluster", nodename=~"$instance"}
)
)

